parameters.jsonBody:
{
  "model": "gpt-4",
  "messages": [
    {
      "role": "system",
      "content": "Você é um consultor executivo de construção civil. Analise os dados das obras e gere insights: 1) Status geral 2) Obras críticas 3) Oportunidades 4) Recomendações estratégicas"
    },
    {
      "role": "user",
      "content": "Dados das obras: {{ JSON.stringify($node['Buscar Obras'].json.data) }}"
    }
  ],
  "max_tokens": 1000
}

parameters.responseBody:
{
  "success": true,
  "dashboard": {
    "obras": {{ JSON.stringify($node['Buscar Obras'].json.data) }},
    "insights": "{{ $node['Gerar Insights IA'].json.choices[0].message.content }}",
    "estatisticas": {
      "total_obras": {{ $node['Buscar Obras'].json.data.length }},
      "obras_no_prazo": {{ $node['Buscar Obras'].json.data.filter(o => o.dias_restantes > 30).length }},
      "obras_criticas": {{ $node['Buscar Obras'].json.data.filter(o => o.problemas_abertos > 3).length }}
    },
    "timestamp": "{{ new Date().toISOString() }}"
  }
}
